// api/ideas/index.ts
import { Client } from '@notionhq/client'

export default async function handler(req, res) {
  const notion = new Client({ auth: process.env.NOTION_API_KEY })
  const response = await notion.databases.query({
    database_id: process.env.NOTION_DATABASE_ID,
    sorts: [{ property: 'Created Date', direction: 'descending' }]
  })
  const ideas = response.results.map(parseNotionPage)
  res.json(ideas)
}

src/hooks/useIdeas.ts 생성:

pseudocode:
import { useQuery } from '@tanstack/react-query'
import type { Idea } from '@/types/idea'

async function fetchIdeas(): Promise<Idea[]> {
  const res = await fetch('/api/ideas')
  return res.json()
}

async function fetchIdeaById(id: string): Promise<Idea> {
  const res = await fetch(`/api/ideas/${id}`)
  return res.json()
}

export function useIdeas() {
  return useQuery({ queryKey: ['ideas'], queryFn: fetchIdeas })
}

export function useIdeaById(id: string) {
  return useQuery({ queryKey: ['ideas', id], queryFn: () => fetchIdeaById(id) })
}

